import React, { useContext, useEffect, useState } from 'react'
import { ShopContext } from '../context/ShopContex'
import { assets } from '../assets/assets';
import { Title } from '../components/Title'
import { ProductItem } from '../components/ProductItem'

function Collection() {
  const { products } = useContext(ShopContext);

  const [showFilter, setShowFilter] = useState(false);

  const [filterProducts, setFilterProducts] = useState([]);

  const [category, setCategory] = useState([]);
  const [subCategory, setSubCategory] = useState([]);

  const [sortType, setSortType] = useState('relvent')

  const Collections = {
    "Men’s Clothing": [
      {
        image: "data:image/jpeg;base64,",
        name: "Unisex T-Shirt Classic",
      },
      {
        image: "https://www.shutterstock.com/image-photo/young-man-wearing-winter-clothes-260nw-1022031901.jpg", // Placeholder image link for Oversized Classic T-Shirt
        name: "Oversized Classic T-Shirt",
      },
      {
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRAfPBTU2mDoscEFPWpI22CbeoljWPRYl8cQlUZ-6dbheglv9iCUfAITvfd9L4Tf8PKSvg&usqp=CAU", // Placeholder image link for Hooded SweatShirt
        name: "Hooded SweatShirt",
      },
    ],
    "Women’s Clothing": [
      {
        image: "https://img.freepik.com/free-photo/young-brunette-woman-with-sunglasses-urban-background_1139-893.jpg", // Placeholder image link for Dresses
        name: "Dresses",
      },
      {
        image: "data:image/jpeg;base64,", // Placeholder image link for Unisex T-Shirt Classic // Placeholder image link for Blouses
        name: "Blouses",
      },
      {
        image: "https://www.shutterstock.com/image-photo/outdoor-fashion-portrait-two-young-260nw-1192869748.jpg", // Placeholder image link for Skirts
        name: "Skirts",
      },
      {
        image: "https://img.freepik.com/free-photo/woman-summer-style-walking-street_285396-2873.jpg",
        name: "Combo"
      }, {
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBzUf11hTrwByQh2T-XumS-NwXGx3B8uiqTWXWkEEsZNSQ0JLfVqUoLOyv9emZjYryuX0&usqp=CAU",
        name: "T-Shirts"
      }
    ],
    "Kid’s Clothing": [
      {
        image: "data:image/jpeg;base64,", // Placeholder image link for Kids' T-Shirts
        name: "Kids' T-Shirts",
      },
      {
        image: "https://img.freepik.com/free-photo/cute-children-having-fun_1157-32483.jpg", // Placeholder image link for Kids' Shorts
        name: "Kids' Shorts",
      },
      {
        image: "https://bluejay.com.my/wp-content/uploads/2021/12/Kids-clothes-Hong-Kong-seed.jpg", // Placeholder image link for Kids' Jackets
        name: "Kids' Jackets",
      },
      {
        image: "https://static.vecteezy.com/system/resources/thumbnails/027/110/402/small_2x/fashion-model-kids-free-photo.jpg",
        name: "Collections"
      }
    ],
  };

  

  const toogleCategory = (e) => {
    if (category.includes(e.target.value)) {
      setCategory(prev => prev.filter(item => item !== e.target.value))
      setSubCategory((prev) => {
        return prev.filter(
          (item) => !Collections[e.target.value].some((subCat) => subCat.name === item)
        );
      });
    } else {
      setCategory(prev => [...prev, e.target.value])
      setSubCategory((prev) => [...prev,...Collections[e.target.value].map((subCat)=>subCat.name)])
    }

    // filter products
    
  }


  const toggleSubCategory = (e) => {
    if (subCategory.includes(e.target.value)) {
      setSubCategory(prev => prev.filter(item => item !== e.target.value))
    } else {
      setSubCategory(prev => [...prev, e.target.value])
    }

    // 
  }


  const applyFilter = () => {
    let productsCopy = products.slice();
    if (category.length > 0) {
      productsCopy = productsCopy.filter(item => category.includes(item.category))
    }

    if (subCategory.length > 0) {
      productsCopy = productsCopy.filter(item => subCategory.includes(item.subCategory))
    }
    setFilterProducts(productsCopy)
  }

  const sortProduct = () => {
    let fpCopy = filterProducts.slice()

    switch (sortType) {
      case 'low-high':
        setFilterProducts(fpCopy.sort((a, b) => (a.price - b.price)));
        break;
      case 'high-low':
        setFilterProducts(fpCopy.sort((a, b) => (b.price - a.price)));
        break;
      default:
        applyFilter();
        break;
    }

  }


  useEffect(() => {
    applyFilter();
  }, [category, subCategory])


  useEffect(() => {
    sortProduct()
  }, [sortType])
  return (

    <div className='flex flex-col sm:flex-row gap-1 sm:gap-10 pt-10 border-t p-10'>
      {/* Filter Options */}
      <div className='min-w-60'>
        <p onClick={() => setShowFilter(!showFilter)} className='my-2 text-xl flex items-center cursor-pointer gap-2'> FILTERS
          <img className={`h-3 sm:hidden ${showFilter ? 'rotate-90' : ''}`} src={assets.dropdown_icon} alt="" />
        </p>

        {/* Category Filter */}
        <div className={`border border-gray-300 pl-5 py-3 mt-6 ${showFilter ? '' : 'hidden'} sm:block `}>
          <p className='mb-3  text-sm font-medium'>CATEGORIES</p>

          <div className='flex flex-col gap-2 text-sm font-light text-gray-700'>
            {
              Object.keys(Collections).map((col, index) => (
                <p className='flex gap-2' key={index}>
                  <input type="checkbox" value={col} onChange={toogleCategory} /> {col}
                </p>
              ))
            }
          </div>

        </div>

        {/* Sub Category filter */}
        <div className={`border border-gray-300 pl-5 py-3 mt-6 ${showFilter ? '' : 'hidden'} sm:block `}>
          <p className='mb-3  text-sm font-medium'>TYPE</p>
          <div className='flex flex-col gap-2 text-sm font-light text-gray-700'>
            {
              subCategory.map((subCat, index) => (
                <p className='flex gap-2' key={index}>
                <input type="checkbox" value={subCat} onChange={toggleSubCategory} /> {subCat}
              </p>
              ))
            }

          </div>
        </div>
      </div>

      {/* Right side */}
      <div className='flex-1'>
        <div className='flex justify-between text-base sm:text-2xl mb-4'>
          <Title text1={"ALL"} text2={' COLLECTIONS'} />
          {/* Porduct sort */}
          <select onChange={(e) => setSortType(e.target.value)} className='border-2 border-gray-300 text-sm px-2 rounded-md'>
            <option value="relavent">Sort by: Relavent</option>
            <option value="low-high">Sort by: Low to High</option>
            <option value="high-low">Sort by: High to Low</option>
          </select>
        </div>

        {/* Map Products */}
        <div className='grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 gap-y-6'>
          {
            filterProducts.map((item, index) => (
              <ProductItem key={index} name={item.name} id={item._id} price={item.price} image={item.image} />
            ))
          }
        </div>

      </div>

    </div>
  )
}

export default Collection
